import React from 'react'
import PropTypes from 'prop-types'
// import { Link } from 'react-router-dom/cjs/react-router-dom.min'




export default function TopBar(props) {
  return (
    // navbar-dark bg-dark is used for changing the light color of navigation bar
    <nav className={`navbar navbar-expand-lg navbar-${props.mode} bg-${props.mode}`}>  
    <div className="container-fluid">
        <a className="navbar-brand" href="#">{props.title}</a>
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
      <div className="collapse navbar-collapse" id="navbarSupportedContent">
        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
          <li className="nav-item">
            <a className="nav-link active" aria-current="page" href="#">Home</a>
          </li>
          {/* <li className="nav-item">
            <Link className="nav-link" to="/about">{props.aboutText}</Link>
          </li> */}
          
        </ul>
        {/* <form className="d-flex" role="search">
          <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
          <button type="button" className="btn btn-outline-info">Search</button>
          <button className="btn btn-outline-success" type="submit">Search</button>

        </form> */}
       <div className={`form-check form-switch text-${props.mode==='light'?'dark':'light'}`}>
          <input className="form-check-input" onClick={props.toggleMode} type="checkbox" role="switch" id="flexSwitchCheckDefault"/>
          <label className="form-check-label" htmlFor="flexSwitchCheckDefault">Enable DarkMode</label>
       </div>
       {/* <div className={`form-check form-switch text-${props.mode==='light'?'dark':'light'}`}>
          <input className="form-check-input" onClick={props.redMode} type="checkbox" role="switch" id="flexSwitchCheckDefault"/>
          <label className="form-check-label" htmlFor="flexSwitchCheckDefault">DarkRed Mode</label>
        </div> */}
      </div>
    </div>
  </nav>
  )
}

TopBar.prototypes = {
  title: PropTypes.string.isRequired,
  aboutText: PropTypes.string.isRequired,
}
// use these default values for props if not assigned in app.js (for this case).
TopBar.defaultProps = {
  title: 'Set title here',
  aboutText: 'About'
};
